package GlobalMembers;

public class SProgramP1 {
	static byte b;
	static short s;
	static int i;
	static long l;
	static float f;
	static double d;
	static char c;
	static boolean b1;
	static String s1;
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("integer default values");
		System.out.println(b);
		System.out.println(s);
		System.out.println(i);
		System.out.println(l);
		System.out.println("***********");
		System.out.println("decimal default values");
		System.out.println(f);
		System.out.println(d);
		System.out.println("***********");
		System.out.println("character default values");
		System.out.println(c);
		System.out.println("***********");
		System.out.println("boolean default values");
		System.out.println(b1);
		System.out.println("***********");
		System.out.println("String default values");
		System.out.println(s1);
	}

}
